 import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import axios from "axios"

import LinkInClass from "../../components/LinkInClass"
import {ACCESS_LEVEL_ADMIN, ACCESS_LEVEL_GUEST, SERVER_HOST} from "../../config/global_constants"


export default class login extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            email:"",
            password:"",
            isLoggedIn:false,
            users: [],
            errors: { // used to keep track of current validation errors
                email: [],
                password: []
            }
        }
    }
    componentDidMount() {
        axios.get(`${SERVER_HOST}/users`)
            .then(res =>
            {
                if(res.data)
                {
                    if(res.data.errorMessage) {
                        console.log(res.data.errorMessage)
                    } else {
                        console.log("Users read to Login page")
                        this.setState({users: res.data})
                        //console.log("users: ",this.state.users)
                    }
                } else {
                    console.log("Users not found")
                }
            })

    }


    handleChange = (e) =>
    {
        this.setState({[e.target.name]: e.target.value})
    }

    validateUserLogin = ()=> {

        let emailIsValid = false;
        let passwordIsValid = false;
        let canLogin = false;
        let email = this.state.email;
        let password= this.state.password

        if(email.trim() && password.trim()){
            this.state.users.forEach(user => {
                if(user.email === email) { // email is found within database
                    console.log("Email found within users collection")
                    emailIsValid = true;
                    if(user.password === password) { // password matches
                        console.log("Password matches")
                        passwordIsValid = true
                    } else {
                        //TODO Display message to user saying their password is wrong
                        console.log("Password does not match")
                    }
                }
            })
            if(emailIsValid && passwordIsValid) {
                canLogin = true;
            }
        } else {
            if(email.trim())
            {
                console.log("email field is empty")
            }
            if(password.trim())
            {
                console.log("pass word field is empty")
            }
        }


        return canLogin;
    }


    handleSubmit = (e) =>
    {
        if(this.validateUserLogin) { //if this returns true, user details passed validation, login user
            axios.post(`${SERVER_HOST}/users/Login/login/${this.state.email}/${this.state.password}`)
                .then(res =>
                {
                    if(res.data)
                    {
                        if (res.data.errorMessage)
                        {
                            console.log(res.data.errorMessage)
                        }
                        else // user successfully logged in
                        {
                            if (res.data.email==="admin@admin.com"){
                                res.data.accessLevel=ACCESS_LEVEL_ADMIN
                            }
                            console.log("User logged in")
                            sessionStorage.name=res.data.name
                            sessionStorage.accessLevel=res.data.accessLevel

                            this.setState({isLoggedIn:true})
                        }
                    }
                    else
                    {
                        console.log("Login failed")
                    }
                })
        } else {

        }

    }


    render()
    {
        return (
            <div className="register-container">
                <h2>Login</h2>
                <form className="login-form" noValidate = {true} id = "loginOrRegistrationForm">

                    {this.state.isLoggedIn ? <Redirect to="/TestingDirectory"/> : null}

                    <input
                        type = "email"
                        name = "email"
                        placeholder = "Email"
                        autoComplete="email"
                        value={this.state.email}
                        onChange={this.handleChange}
                    /><br/>

                    <input
                        type = "password"
                        name = "password"
                        placeholder = "Password"
                        autoComplete="password"
                        value={this.state.password}
                        onChange={this.handleChange}
                    /><br/><br/>

                    <LinkInClass value="Login" className="green-button" onClick={this.handleSubmit}/>
                    <Link className="red-button" to={"/TestingDirectory"}>Cancel</Link>
                </form>

                <div className="testing-return"><Link className="testing-red-button" to={"/TestingDirectory`"}>RETURN</Link></div>
            </div>

        )
    }
}
