        //             this.props.products.filter(product => {
        //             return product.size.includes(filters.size[j])
        //         }))
        //     }
        // }

    }

    // Helper function to get the values from a checkbox group and add them
    // to their respective arrays
    getCheckedValues = (filterName) => {
        let checkboxes = document.getElementsByName(filterName)
        let checkedValues = []

        checkboxes.forEach((checkbox) => {
            if(checkbox.checked) {
                checkedValues.push(checkbox.value)
            }
        })
        return checkedValues
    }


    render() {
        return (

                <div className = "filter-container" >
                    <fieldset>

                        <h4>Gender</h4>
                        <div className="checkbox-filters">
                            <label> M
                                <input
                                    type="checkbox"
                                    name="gender"
                                    value="Male"/>
                            </label>

                            <label> F
                                <input
                                    type="checkbox"
                                    name="gender"
                                    value="Female"/>
                            </label>

                            <label> O
                                <input
                                    type="checkbox"
                                    name="gender"
                                    value="Other"/>
                            </label>
                        </div>

                        <h4>Size</h4>
                        <div>
                            <label> S
