                                    type="checkbox"
                                    name="colour"
                                    value="Purple"/>
                            </label>
                            <label> White
                                <input
                                    type="checkbox"
                                    name="colour"
                                    value="White"/>
                            </label>
                            <label> Pink
                                <input
                                    type="checkbox"
                                    name="colour"
                                    value="Pink"/>
                            </label>
                            <label> Yellow
                                <input
                                    type="checkbox"
                                    name="colour"
                                    value="Yellow"/>
                            </label>
                        </div>

                        <h4>Price</h4>
                        <div className="checkbox-filters">
                            <input type="range" name="price-filter"/>
                        </div>

                        <div className="checkbox-button">
                           <button type="button" onClick={this.getFilters}>Apply</button>
                        </div>
                    </fieldset>
                </div>
        )
    }
}
