import React, {Component} from "react"
import NavBar from "./NavBar";
import pinkTshirt from "../tshirts-images/p10/front.png";
import {Link, Redirect} from "react-router-dom";
import axios from "axios"
import LinkInClass from "../../components/LinkInClass"
import Footer from "./Footer";
import {SERVER_HOST, ACCESS_LEVEL_GUEST} from "../config/global_constants";



export default class AccountPage extends Component {
    constructor(props)
    {
        super(props)

        this.state = {
            email:"",
            password:"",
            isLoggedIn:localStorage.accessLevel > ACCESS_LEVEL_GUEST,
            users: [],
            errors: { // used to keep track of current validation errors
                email: [],
                password: []
            }
        }
    }
    componentDidMount() {

        axios.get(`${SERVER_HOST}/users`)
            .then(res =>
            {
                if(res.data)
                {
                    if(res.data.errorMessage) {
                        console.log(res.data.errorMessage)
                    } else {
                        console.log("Users read to Login page")
                        this.setState({users: res.data})
                        //console.log("users: ",this.state.users)
                    }
                } else {
                    console.log("Users not found")
                }
            })

    }
    handleChange = (e) =>
    {
        this.setState({[e.target.name]: e.target.value})

    }


    validateUserLogin() {
        let canLogin = false;
        let emailErrors = []
        let pwErrors = []
        let email = this.state.email
        let pw = this.state.password
        if(!email.trim())
        {
            emailErrors.push("Email cannot be empty")
        }
        this.setState(prevState => ({
            errors: {
                ...prevState.errors,
                email: emailErrors
            }
        }))
        if(!pw.trim())
        {
            pwErrors.push("Password cannot be empty")
        }
        this.setState(prevState => ({
            errors: {
                ...prevState.errors,
                password: pwErrors
            }
        }))

        if (emailErrors.length === 0 && pwErrors.length === 0)
        { // if there are no errors
            {
                canLogin = true
                this.setState(prevState => ({ // else set the state of errors.email to an empty array
                    errors: {
                        ...prevState.errors,
                        email: []
                    }
                }))
                this.setState(prevState => ({ // else set the state of errors.email to an empty array
                    errors: {
                        ...prevState.errors,
                        password: []
                    }
                }))
            }

        }
        console.log("canLogin = ", canLogin)
        return canLogin;
    }


    handleSubmit = (e) => {
        let loginErrors = []
        e.preventDefault()

        if(this.validateUserLogin()) { //if this returns true, user details passed validation, login user

            axios.post(`${SERVER_HOST}/users/AccountPage${this.state.email}/${this.state.password}`)

                .then(res =>
                {
                    if(res.data)
                    {
                        if (res.data.errorMessage)
                        {
                            this.state.isPasswordWrong = true
                        }
                        else // user successfully logged in
                        {
                            this.state.isPasswordWrong = false


                            console.log("User logged in")
                            localStorage.name = res.data.name
                            localStorage.email = this.state.email
                            localStorage.accessLevel = res.data.accessLevel
                            localStorage.profilePhoto=res.data.profilePhoto
                            localStorage.token = res.data.token

                            this.setState({isLoggedIn: true})
                        }
                    } else {
                        console.log("Error logging in")
                    }
                })
        } else {
            console.log("login denied invalid credentials")
        }
    }

    render() {
        return (
            <div>

                <div className="account-head-container">
                    <NavBar/>
                </div>
                    <div className="account-container">
                        <div className="account-box">
                            <div className="login-box">
                                <h1>WELCOME BACK!</h1>
                                <label> Email Address:<span> *</span> </label>
                                <input type="text" id="email-input"/><br/><br/>
                                <label>Password:<span> *</span></label>
                                <input type="password" id="password-input"/>
                                <div className="register-box">
                                    <Link to={"/Login/login"}>
                                        <button className={"signIn-btn"}>SIGN IN</button>
                                    </Link>
                                </div>
                            </div>
                            <div className="vl"></div>
                            <div className="register-box">
                                <div className={"no-account-container"}>
                                    <h1>DON'T HAVE AN ACCOUNT?</h1>
                                    <Link to={"/Login/Register"}>
                                        <button className={"register-btn"}>CREATE AN ACCOUNT</button>
                                    </Link>
                                    {/*Continue as guest would take you back to the main page - using the guest features*/}
                                    <Link to={"/MainPage"}>
                                        <button className={"guest-btn"}>CONTINUE AS GUEST</button>
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                        <footer>
                            <Footer/>
                        </footer>
                    </div>
                    )
                }
                }
