import React, {Component} from "react"
import {Link} from "react-router-dom"


export default class AdminTableRow extends Component
{
    constructor(props) {
        super(props)
        this.state = {
            rowNum: 1,
        }
    }

    componentDidMount() {
        // Increase the row number for each call to this component
        this.setState((prevState) => {
            rowNum: prevState.rowNum + 1
        })
    }

    render() {
        const {
            product_id,
            name,
            price,
            category,
            brand,
            current_stock,
        } = this.props.products;
        return (
            <tr>
                <td>{this.state.rowNum}</td>
                <td>{name}</td>
                <td>{price}</td>
                <td>{category}</td>
                <td>{brand}</td>
                <td>{product_id}</td>
                <td>{current_stock}</td>
                <td>
                    <Link className="green-button" to={"/EditTShirt/" + this.props.products.product_id}>
                        Edit
                    </Link>
                    <Link className="red-button" to={"/DeleteTShirt/" + this.props.products.product_id}>
                        Delete
                    </Link>
                </td>
            </tr>
        )
    }
}
