import React, {Component} from "react"
import axios from "axios";
import {SERVER_HOST} from "../../config/global_constants";
import {Redirect} from "react-router-dom";
import Navbar from "../NavBar";

export default class DeleteUsers extends Component {
    constructor(props) {
        super(props)

        this.state = {
            user: [],
            redirectToUserDashboard: false
        }
    }
    componentDidMount() {
        const userID = this.props.match.params._id // get productID passed from redirect parameters
        //console.log(productID)

        // get the product with the matching id from database collection
        axios.get(`${SERVER_HOST}/users/${userID}`,{headers:{"authorization":localStorage.token}})
            .then(res => {
                if(res.data) {
                    if(res.data.errorMessage) {

                    } else {
                        console.log("User found and displaying in ViewUsers")
                        this.setState({user: res.data}) // set state of product to response data
                    }
                } else {
                    console.log("User not found")
                }
            })
    }


    handleDeleteProduct = () => {
        const userID = this.props.match.params._id // get productID passed from redirect parameters
        //console.log(productID)
        axios.delete(`${SERVER_HOST}/users/${userID}`, {headers:{"authorization":localStorage.token}})
            .then (res =>
            {
                if(res.data) {
                    if(res.data.errorMessage) {

                    } else {
                        console.log("Product has been deleted")
                    }
                } else {
                    console.log("Product not deleted")
                }
            })
        this.setState({redirectToUserDashboard: true})
    }



    render() {
        return (
            <div>
                {this.state.redirectToUserDashboard ? <Redirect to={"/AdminDashboard/ViewUsers"}/> : null }

                <div className="admin-head-container">
                    <Navbar/>
                </div>
                <div className="admin-edit-product">
                    <h1>Update Product</h1>
                    <form className="edit-form" >

                        <div className="edit-input">
                            <label>Name</label>
                            <input
                                type="text"
                                name="name"
                                value={this.state.product.name}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="edit-input">
                            <label>Colour</label>
                            <input
                                type="text"
                                name="colour"
                                value={this.state.product.colour}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="edit-input">
                            <label>Price</label>
                            <input
                                type="text"
                                name="price"
                                value={this.state.product.price}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="edit-input">
                            <label>Gender</label>
                            <input
                                type="text"
                                name="gender"
                                value={this.state.product.gender}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="edit-input">
                            <label>Category</label>
                            <input
                                type="text"
                                name="category"
                                value={this.state.product.category}
                                onChange={this.handleChange}
                            />
                        </div>
                        <div className="edit-input">
                            <label>Brand</label>
                            <input
                                type="text"
                                name="brand"
                                value={this.state.product.brand}
                                onChange={this.handleChange}
                            />
                        </div>

                        <button onClick={this.handleUpdateProduct}>Update</button>
                        <button onClick={this.handleDeleteProduct}>Delete</button>
                        <button onClick={this.handleReturn}>Return</button>

                    </form>
                </div>
            </div>
        )
    }


}
